import {
  Button,
  Typography,
  Grid,
  Select,
  MenuItem,
  TextField,
  TableContainer,
  Divider,
  Box,Table, TableHead, TableBody, TableRow, TableCell
} from "@material-ui/core";

import { Scrollbars } from "react-custom-scrollbars";
import React, { Component } from "react";
import { withStyles } from "@material-ui/styles";
import { KeyboardArrowDown, NoEncryptionTwoTone } from "@material-ui/icons";
import DatePicker from "react-date-picker";
import moment from 'moment'

const myStyle = () => ({
  buttonText: {
    fontSize: "13px",
    color: "#fff",
    fontWeight: 700,
  },
  btn: {
    padding: "3px 50px",
    borderRadius: "14px",
    "&:hover": {
      background: "##5696F4",
    },
  },
  reportPeriodText: {
    fontSize: "20px",
    fontWeight: 600,
  },
  selectCustom: {
    height: "40px",
    width: "200px",
    borderRadius: "4px",
  },
  btnRunReport: {
    padding: "3px 10px",
    borderRadius: "14px",
    background: "#5696F4",
    "&:hover": {
      background: "#5696F4",
    },
  },
  reportDatePicker: {
    "& div": {
      borderColor: "grey",
      borderRadius: "5px",
      padding: "5px",
      background: "#f4efef",
    },
  },
  popoverPaper: {
    backgroundColor: "lightgreen",
    borderRadius: "16px",
    "& .MuiMenuItem-root": {
      backgroundColor: "red",
    },
    "& .MuiMenuItem-root.Mui-selected": {
      backgroundColor: "blue",
    },
  },
  reportTable:{
    "& .MuiTableHead-root":{

         background:"khaki"
    },
    "& .MuiTableCell-root":{
      borderRight : "1px solid #bababa",
         padding : "5px" 
    }
  }
  // outlined:{
  //   border : "5px solid red"
  // }
});

class Reports extends Component {
  componentDidMount() {
    console.log("Newdashboards componentDidMount");
  }
  componentWillUnmount() {
    console.log("Newdashboards unmount");
  }
